import React from 'react';
import { Card } from 'react-bootstrap';
import Rating from './Rating';
import { Link } from 'react-router-dom';

function Product({ product }) {
  // Tính phần trăm giảm giá
  const discountPercentage = Math.floor(((product.initial_price - product.price) / product.initial_price) * 100);

  return (
    <Card className="my-3 p-3 rounded">
      <div className="discount-percentage">{discountPercentage}% OFF</div>
      <Link to={`/product/${product._id}`}>
        <Card.Img src={`http://127.0.0.1:8000${product.image}`} />
      </Link>
      <Card.Body>
        <Link to={`/product/${product._id}`}>
          <Card.Title as="div">
            <strong>{product.name}</strong>
          </Card.Title>
        </Link>
        <Card.Text as="div">
          <div className="my-3">
            <Rating value={product.rating} text={`${product.numReviews} reviews`} color={'#f8e825'} />
          </div>
        </Card.Text>
        <Card.Text as="h3">
          <span className="original-price">{new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(product.initial_price)}</span>
          <span className="discounted-price">{new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(product.price)}</span>
        </Card.Text>
      </Card.Body>
    </Card>
  );
}

export default Product;
