import React, {useState, useEffect, useId} from 'react';
import {Form, Button, Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector} from 'react-redux';
import Loader from '../components/Loader';
import Message from '../components/Message';
import FormContainer from '../components/FormContainer';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import { productDetailAction, productUpdateAction } from '../actions/productActions';
import { productUpdateSlice } from '../reducers/productSlice';
import axios from 'axios';

function ProductEditScreen() {
  const {id} =  useParams();
  const productId = id
  const [name, setName] = useState('');
  const [price, setPrice] = useState(0);
  const [image, setImage] = useState('');
  const [brand, setBrand] = useState('');
  const [category, setCategory] = useState('');
  const [countInStock, setCountInStock] = useState(0);
  const [description, setDescription] = useState('');
  const [uploading, setUploading] = useState(false);

  const dispatch = useDispatch();
  const productDetail = useSelector(state => state.productDetail);
  const {loading, product, error} = productDetail;

  const productUpdate = useSelector(state => state.productUpdate);
  const {loading: loadingUpdate, success: successUpdate, error: errorUpdate} = productUpdate;

  const navigate = useNavigate();

  useEffect(() => {

      if(successUpdate)
      {
          dispatch(productUpdateSlice.actions.Reset());
          navigate('/admin/productlist');
      }
      else
      {
          if(!product || (product && product._id !== Number(productId)))
          {
              dispatch(productDetailAction(productId));
          }
          else
          {
              setName(product.name);
              setPrice(product.price);
              setImage(product.image);
              setBrand(product.brand);
              setCategory(product.category);
              setCountInStock(product.countInStock);
              setDescription(product.description);
          }
      }
  }, [dispatch, productId, product, navigate, successUpdate]);
  const submitHandler = (e) =>{
      e.preventDefault();
      dispatch(productUpdateAction({_id: productId, name, price, image, brand, category, countInStock, description}));
  }

  const uploadFileHandler = async (e) =>{
      console.log('upload file');
  }

  return (
    <div>
      <Link to={`/admin/productlist`} className='btn btn-light my-3'>
        Go Back
      </Link>
      <FormContainer>
        <h1>Edit Product</h1>
        {loadingUpdate && <Loader/>}
        {errorUpdate && <Message variant='danger'>{errorUpdate}</Message>}
        {loading ? <Loader/> : error ? <Message variant='danger'>{error}</Message> :
          (
            <Form onSubmit={submitHandler}>
              <Form.Group controlId='name'>
                <Form.Label>Name</Form.Label>
                <Form.Control 
                  type='name' 
                  placeholder='Enter name' 
                  value={name} 
                  onChange={(e) => setName(e.target.value)}
                  >
                  </Form.Control>
              </Form.Group>
              <Form.Group controlId='price'>
                <Form.Label>Price</Form.Label>
                <Form.Control 
                  type='number' 
                  placeholder='Enter price' 
                  value={price} 
                  onChange={(e) => setPrice(e.target.value)}
                  >
                  </Form.Control>
              </Form.Group>
              <Form.Group controlId='image'>
                <Form.Label>Image</Form.Label>
                <Form.Control 
                  type='text' 
                  placeholder='Enter image url' 
                  value={image} 
                  onChange={(e) => setImage(e.target.value)}
                  >
                  </Form.Control>
                  <Form.File 
                  id='image-file' 
                  label='Choose File' 
                  custom
                  onChange={uploadFileHandler}>
                  </Form.File>
              </Form.Group>
              <Form.Group controlId='brand'>
                <Form.Label>Brand</Form.Label>
                <Form.Control 
                  type='text' 
                  placeholder='Enter brand' 
                  value={brand} 
                  onChange={(e) => setBrand(e.target.value)}
                  >
                  </Form.Control>
              </Form.Group>
              <Form.Group controlId='countInStock'>
                <Form.Label>Count In Stock</Form.Label>
                <Form.Control 
                  type='number' 
                  placeholder='Enter countInStock' 
                  value={countInStock} 
                  onChange={(e) => setCountInStock(e.target.value)}
                  >
                  </Form.Control>
              </Form.Group>
              <Form.Group controlId='category'>
                <Form.Label>Category</Form.Label>
                <Form.Control 
                  type='text' 
                  placeholder='Enter category' 
                  value={category} 
                  onChange={(e) => setCategory(e.target.value)}
                  >
                  </Form.Control>
              </Form.Group>
              <Form.Group controlId='description'>
                <Form.Label>Description</Form.Label>
                <Form.Control 
                  type='text' 
                  placeholder='Enter description' 
                  value={description} 
                  onChange={(e) => setDescription(e.target.value)}
                  >
                  </Form.Control>
              </Form.Group>
              <Button type='submit' variant='primary'>Update</Button>
            </Form>
          )
        }

      </FormContainer>
    </div>
  )
}

export default ProductEditScreen
