import { createSlice } from "@reduxjs/toolkit";

export const productHomeSlice = createSlice({
    name: 'productHome',
    initialState:{
        products: [],
        loading: true,
        error: ''
    },
    reducers: {
        Request: (state) => {
            state.loading = true;
            state.products = [];
        },
        Success: (state, action) => {
            state.loading = false;
            state.products = action.payload;
        },
        Fail: (state, action) => {
            state.loading = false;
            state.error = action.payload;
        }
    }
})
