import React, {useState, useEffect} from 'react';
import {Form, Button, Row, Col, Table } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import { useDispatch, useSelector} from 'react-redux';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { getUserDetail, updateUserProfile } from '../actions/userActions';
import { useLocation, useNavigate } from 'react-router-dom';
import { userUpdateProfileSlice } from '../reducers/userSlice';
import { listMyOrders } from '../actions/orderActions';

function ProfileScreen() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [message, setMessage] = useState('');
    const navigation = useNavigate();
    const dispatch = useDispatch();
    
    const userDetail = useSelector(state => state.userDetail);
    const {loading, user, error} = userDetail;

    const userLogin = useSelector(state => state.userLogin);
    const userInfo = userLogin.userInfo;
    const userUpdateProfile = useSelector(state => state.userUpdate);
    const { success } = userUpdateProfile;
    const orderListMy = useSelector(state => state.orderMyList);
    const { loading: loadingOrders, error: errorOrders, orders } = orderListMy;
    
    useEffect(() => {
        if(!userInfo)
        {
            navigation('/login');
        }
        else
        {
            if(!user || !user.name || success)
            {
                dispatch(userUpdateProfileSlice.actions.Reset());
                dispatch(getUserDetail('profile'));
                dispatch(listMyOrders());
            }
            else
            {
                console.log(user.name, user.email)
                setName(user.name);
                setEmail(user.email);
            }
        }
    }, [dispatch, navigation, userInfo, user, success]);

    const submitHandler = (e) =>{
        e.preventDefault();
        if(password !== confirmPassword){
            setMessage('Passwords do not match');
        }
        else{
            setMessage('');
            dispatch(updateUserProfile({
                'id': user.id, 
                'name': name, 
                'email': email, 
                'password': password}));
        }
    }
    const [showPasswordFields, setShowPasswordFields] = useState(false);
    const handleUpdateProfileClick = () => {
        setShowPasswordFields(true);
    }
    return (
        <Row>
            <Col md={3}>
                <h2>User Profile</h2>
                {message && <Message variant='danger'>{message}</Message>}
                {error && <Message variant='danger'>{error}</Message>}
                {loading && <Loader/>}
                <Form onSubmit={submitHandler}>
                <Form.Group controlId='name'>
                    <Form.Label>Name</Form.Label>
                    <Form.Control 
                        required
                        type='name'
                        placeholder='Enter name' 
                        value={name} 
                        onChange={(e) => setName(e.target.value)}
                        readOnly={!showPasswordFields}
                    />
                    </Form.Group>
                    <Form.Group controlId='email'>
                        <Form.Label>Email Address</Form.Label>
                        <Form.Control 
                            required
                            type='email'
                            placeholder='Enter email' 
                            value={email} 
                            onChange={(e) => setEmail(e.target.value)}
                            readOnly={!showPasswordFields}
                        />
                    </Form.Group>
                    {showPasswordFields && (
                        <>
                            <Form.Group controlId='password'>
                                <Form.Label>Password</Form.Label>
                                <Form.Control 
                                    type='password'
                                    placeholder='Enter password' 
                                    value={password} 
                                    onChange={(e) => setPassword(e.target.value)}
                                />
                            </Form.Group>
                            <Form.Group controlId='confirmPassword'>
                                <Form.Label>Confirm Password</Form.Label>
                                <Form.Control 
                                    type='password'
                                    placeholder='Confirm password' 
                                    value={confirmPassword} 
                                    onChange={(e) => setConfirmPassword(e.target.value)}
                                />
                            </Form.Group>
                            <Button type='submit' variant='primary'>Submit</Button>
                        </>
                    )}
                    {!showPasswordFields && (
                        <Button type='button' variant='primary' onClick={handleUpdateProfileClick}>Update Profile</Button>
                    )}
                </Form>
            </Col>
            <Col md={9}>
                <h2>My Orders</h2>
                {loadingOrders ? <Loader/> : 
                errorOrders ? <Message variant='danger'>{errorOrders}</Message> : 
                (
                    <Table striped responsive className="table-sm">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Date</th>
                                <th>Total</th>
                                <th>Paid</th>
                                <th>Delivered</th>
                                <th></th>
                            </tr>
                        </thead>
                        <tbody>
                            {orders.map(order => (
                                <tr key={order._id}>
                                    <td>{order._id}</td>
                                    <td>{order.created_at.substring(0, 10)}</td>
                                    <td>{order.totalPrice}</td>
                                    <td>{order.isPaid ? order.paidAt.substring(0, 10) : (
                                        <i className='fas fa-times' style={{color: 'red'}}></i>
                                    )}</td>
                                    <td>{order.is_delivered ? order.delivered_at.substring(0, 10) : (
                                        <i className='fas fa-times' style={{color: 'red'}}></i>
                                    )}</td>
                                    <td>
                                        <LinkContainer to={`/order/${order.id}`}>
                                            <Button variant='light' className='btn-sm'>Details</Button>
                                        </LinkContainer>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                )}
            </Col>
        </Row>
    )
}

export default ProfileScreen
