import React, {useState, useEffect} from 'react'
import { Link, useParams, useNavigate } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card, Form } from 'react-bootstrap';
import Rating from '../components/Rating';
import { useDispatch, useSelector } from 'react-redux';
import { productDetailAction, productReviewAction } from '../actions/productActions';
import Loader from '../components/Loader';
import Message from '../components/Message';
import { productReviewSlice } from '../reducers/productSlice';

function ProductScreen() {

    const [qty, setQty] = useState(1)
    const [rating, setRating] = useState(0)
    const [comment, setComment] = useState('')
    const { id } = useParams();
    const dispatch = useDispatch();

    const productDetail = useSelector(state => state.productDetail);
    const {product, loading, error} = productDetail;

    const productReview = useSelector(state => state.productReviewCreate);
    const {loading: loadingReview, error: errorReview, success: successReview} = productReview;

    const userLogin = useSelector(state => state.userLogin);
    const {userInfo} = userLogin;

    useEffect(() => {
        dispatch(productDetailAction(id))
        if(successReview){
            setRating(0)
            setComment('')
            dispatch(productReviewSlice.actions.Reset())
        }
    }, [dispatch, id])

    const navigate = useNavigate();
    const addToCartHandler = () => {
        console.log('Add to cart: ', id)
        navigate(`/cart/${id}?qty=${qty}`)
    }


    return (
      <div>
        <Link to='/' className='btn btn-light my-3'>Go Back</Link>
        {   loading ? <Loader/>
            : error ? <Message variant='danger'>{error}</Message>
            : 
            (
                <div>
                    <Row>
                        <Col md={6}>
                            <Image src={`http://127.0.0.1:8000${product.image}`} alt={product.name} fluid />
                        </Col>
                        <Col md={3}>
                            <ListGroup variant='flush'>
                                <ListGroup.Item>
                                    <h3>{product.name}</h3>
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    <Rating value={product.rating} text={`${product.numReviews} reviews`} color={'#f8e825'} />
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    Price: ${product.price}
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    Description: {product.description}
                                </ListGroup.Item>
                            </ListGroup>
                        </Col>
                        <Col md={3}>
                            <Card>
                                <ListGroup>
                                    <ListGroup.Item variant='flush'>
                                        <Row>
                                            <Col>Price: </Col>
                                            <Col>
                                                <strong>${product.price}</strong>
                                            </Col>
                                        </Row>
                                    </ListGroup.Item>
                
                                    <ListGroup.Item variant='flush'>
                                        <Row>
                                            <Col>Status: </Col>
                                            <Col>
                                                {product.countInStock > 0 ? 'In Stock' : 'Out Stock'}
                                            </Col>
                                        </Row>
                                    </ListGroup.Item>
    
                                    {product.countInStock > 0 && (
                                        <ListGroup.Item>
                                            <Row>
                                                <Col>Quantity</Col>
                                                <Col xs='auto' className = 'my-1'>
                                                    <Form.Control
                                                        as="select"
                                                        value={qty}
                                                        onChange={(e) => setQty(e.target.value)}
                                                    >
                                                        {
                                                            [...Array(product.countInStock).keys()].map((x) =>(
                                                                <option key={x + 1} value={x + 1}>
                                                                    {x + 1}
                                                                </option>
                                                            ))
                                                        }
                                                    </Form.Control>
                                                </Col>
                                            </Row>
                                        </ListGroup.Item>
                                    )}
    
                                    <ListGroup.Item>
                                        <Button 
                                            onClick={addToCartHandler}
                                            disabled={product.countInStock === 0} 
                                            className='btn-block w-100' 
                                            type='button'>
                                            Add to Card
                                        </Button>
                                    </ListGroup.Item>
                                </ListGroup>
                            </Card>
                        </Col>
                    </Row>
                    <Row className='py-10'>
                        <Col md={6}>
                            <h4>Review</h4>
                            {product.reviews.length === 0 && <Message variant='info'>No review</Message>}

                        </Col>
                    </Row>
                </div>
            )
        }
      </div>
    );
}

export default ProductScreen;
