import React from 'react';
import { Card } from 'react-bootstrap';
import Rating from './Rating';
import { Link } from 'react-router-dom';

function Product({ product }) {
  // Định dạng giá thành VNĐ
  const formattedPrice = new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(product.original_price);

  return (
    <Card className="my-3 p-3 rounded">
      {/* Thêm phần hiển thị phần trăm giảm giá */}
      {product.discount_percentage && (
        <div className="discount-percentage">
          {product.discount_percentage}% OFF
        </div>
      )}
      <Link to={`/product/${product._id}`}>
        <Card.Img src={`http://127.0.0.1:8000${product.image}`} />
      </Link>
      <Card.Body>
        <Link to={`/product/${product._id}`}>
          <Card.Title as="div">
            <strong>{product.name}</strong>
          </Card.Title>
        </Link>
        <Card.Text as="div">
          <div className="my-3">
            <Rating value={product.rating} text={`${product.numReviews} reviews`} color={'#f8e825'} />
          </div>
        </Card.Text>
        {/* Hiển thị giá ban đầu */}
        <Card.Text as="h3">
          {formattedPrice}
        </Card.Text>
      </Card.Body>
    </Card>
  );
}

export default Product;
