import { productSlice } from "../reducers/productSlice";
import axios from "axios";

export const listProducts = () => async (dispatch) => {
    try {
        // Gọi action creator và dispatch action trả về
        dispatch(productSlice.actions.productHomeRequest());
        const { data } = await axios.get('http://127.0.0.1:8000/api/products/')
        // Gọi action creator với data làm argument và dispatch action trả về
        dispatch(productSlice.actions.productHomeSuccess(data));

    } catch (error) {
        // Tạo message từ error và dispatch action fail
        const errorMessage = error.response && error.response.data.message 
                             ? error.response.data.message : error.message;
        dispatch(productSlice.actions.productFail(errorMessage));
    }
}
