import { orderCreateSlice } from "../reducers/orderSlice";
import axios from 'axios';

export const createOrder = (order) => async (dispatch, getState) => {
    try {
        dispatch(orderCreateSlice.actions.Request());
        const userInfo = getState().userLogin.userInfo;
        const config = {
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${userInfo.token}`
            }
        }
        const { data } = await axios.post('http://127.0.0.1:8000/api/orders/add', order, config);
        dispatch(orderCreateSlice.actions.Success(data));
    } catch (error) {
        dispatch(orderCreateSlice.actions.Fail(error.response && error.response.data.detail ? error.response.data.detail : error.detail));
    }
}
