import { configureStore } from '@reduxjs/toolkit';
import { productHomeSlice } from './reducers/products/productHomeSlice';
import { productDetailAction } from './actions/prodcutActions/productDetailActions';
import { cartSlice } from './reducers/carts/cartSlice';

const store = configureStore({
  reducer: {
    productHome: productHomeSlice.reducer,
    productDetail: productDetailSlice.reducer,
    cart: cartSlice.reducer,
  },
  middleware: (getDefaultMiddleware) => getDefaultMiddleware(),
  devTools: process.env.NODE_ENV !== 'production', // Kích hoạt Redux DevTools trong môi trường không phải production
});

export default store
