import { createSlice } from "@reduxjs/toolkit";

export const productHomeSlice = createSlice({
    name: 'productHome',
    initialState:{
        products: [],
        loading: true,
        error: ''
    },
    reducers: {
        Request: (state) => {
            state.loading = true;
            state.products = [];
        },
        Success: (state, action) => {
            state.loading = false;
            state.products = action.payload;
        },
        Fail: (state, action) => {
            state.loading = false;
            state.error = action.payload;
        }
    }
});

export const productDetailSlice = createSlice({
    name: 'productDetail',
    initialState:{
        product: [],
        loading: true,
        error: ''
    },
    reducers: {
        Request: (state) => {
            state.loading = true;
        },
        Success: (state, action) => {
            state.loading = false;
            state.product = action.payload;
        },
        Fail: (state, action) => {
            state.loading = false;
            state.error = action.payload;
        }
    }
});

export const productDeleteSlice = createSlice({
    name: 'productDelete',
    initialState:{
        loading: true,
        success: false,
        error: ''
    },
    reducers: {
        Request: (state) => {
            state.loading = true;
            state.success = false;
            state.error = '';
        },
        Success: (state) => {
            state.loading = false;
            state.success = true;
            state.error = '';
        },
        Fail: (state, action) => {
            state.loading = false;
            state.success = false;
            state.error = action.payload;
        }
    }
})
